// =========================================================================
// SIPS_v29_VFUSE - THERMAL MODEL + STABLE V28 CORE
// =========================================================================
// Bas: v28 (Geofence, API-budget, KW-limit, Auto-start fungerar)
// Logik: VFUSE (Simulerad porslinss칛kring med tr칬ghet)

const TOPIC_STORAGE = "system/sips/storage"; 

// =========================================================================
// 0. HANTERA INPUT, BACKUP & STATUS (FR칀N v28)
// =========================================================================

// A. L츿S IN BACKUP VID START
if (msg.topic === TOPIC_STORAGE) {
    try {
        let data = msg.payload;
        if (typeof data === "string") data = JSON.parse(data);

        // 칀terst칛ll API r칛knare (v28)
        if (data && data.c !== undefined && data.m !== undefined) {
            flow.set("monthlySignalCount", data.c);
            flow.set("currentMonthIndex", data.m);
        }
        // 칀terst칛ll S칛kringsv칛rme (VFUSE)
        if (data && data.fh) {
            flow.set("fuseHeat", data.fh);
        }

        node.status({fill: "blue", shape: "ring", text: "RESTORED DATA"});
    } catch(e) { node.error("Backup read error: " + e.message); }
    return null;
}

// B. MANUELL RESET
if (msg.topic === "reset_simulation") {
    // Nollst칛ll v28 state
    flow.set("resourceStates", undefined);
    flow.set("teslaState", "unknown");
    flow.set("teslaGeofence", "home"); 
    flow.set("teslaAmps", 0);
    flow.set("teslaMaxAmps", undefined); 
    flow.set("lastHomeyCmdTime", 0);

    // Nollst칛ll VFUSE state
    flow.set("fuseHeat", {1:0, 2:0, 3:0}); 

    // Nollst칛ll Budget
    let nowM = new Date().getMonth();
    flow.set("currentMonthIndex", nowM);
    flow.set("monthlySignalCount", 0);

    let cleanBackup = { topic: TOPIC_STORAGE, payload: JSON.stringify({c: 0, m: nowM, fh:{1:0,2:0,3:0}}), retain: true };
    node.status({fill:"blue", shape:"dot", text:"游댃 RESET COMPLETE"});
    return [cleanBackup, null];
}

// C. STATUS UPPDATERINGAR FR칀N TESLAMATE
if (msg.topic.includes("geofence")) {
    flow.set("teslaGeofence", msg.payload);
    return null;
}
if (msg.topic.includes("state")) {
    let rawState = msg.payload.toString();
    flow.set("teslaState", rawState.toLowerCase()); 
    return null; 
}
if (msg.topic.includes("charge_current_request")) {
    // Hanterar b친de request och request_max
    let val = parseFloat(msg.payload);
    if (msg.topic.endsWith("_max")) flow.set("teslaMaxAmps", val);
    else flow.set("teslaAmps", val);
    return null; 
}

// Om vi inte har m칛tdata, avbryt
if (!msg.payload || !msg.payload.metrics) return null;


// =========================================================================
// 1. KONFIGURATION
// =========================================================================
const DRY_RUN = false; 
const MONTHLY_LIMIT = 5000;
const CRITICAL_CUTOFF = 4900;

// --- NYTT: VFUSE KONFIGURATION ---
const FUSE_NOMINAL = 20;      // Din s칛kring (A)
const FUSE_TAU = 300;         // Tr칬ghet (sekunder)
const TEMP_REGULATE = 0.70;   // 70% v칛rme -> Reglera
const TEMP_CRITICAL = 0.95;   // 95% v칛rme -> Panik
const AMP_PANIC = 40;         // Kortslutningsskydd (A)

// --- EFFEKT (KW) ---
const POWER_LIMIT = flow.get("powerLimit") || 11000; 
const POWER_RESET_BUFFER = 2000;  

// --- TESLA ---
const TESLA_MIN_AMPS = 5;         
const TESLA_DEFAULT_MAX = 13;   
const TESLA_HOME_NAME = "home"; // M친ste matcha Teslamate exakt (oftast "Home" eller "home")
const TESLA_PHASES = [1, 2, 3]; 
const API_COOLDOWN_MINS = 3;    

// Topics
const TOPIC_HOMEY_SET = "homey/input/tesla_set_amps";
const TOPIC_HOMEY_STOP = "homey/input/tesla_stop";
const TOPIC_HOMEY_START = "homey/input/tesla_start";

let resources = [
    { id: "tesla", type: "tesla_homey", shedOrder: 1, name: "Tesla", phases: TESLA_PHASES },
    { id: "vvb", type: "mqtt", shedOrder: 2, topic: "shellies/vvb1/relay/0/command", name: "VVB", phases: [2,3] },
    { id: "rad", type: "mqtt", shedOrder: 3, topic: "shellies/rad1/relay/0/command", name: "Rad", phases: [1,2,3] },
    { id: "avf", type: "mqtt", shedOrder: 4, topic: "shellies/avf1/relay/0/command", name: "Avf", phases: [1] }
];


// =========================================================================
// 2. TIDS- OCH STATUS-HANTERING
// =========================================================================
let now = new Date();
let currentMonthIndex = flow.get("currentMonthIndex");
let monthlyCount = flow.get("monthlySignalCount") || 0;

if (currentMonthIndex === undefined || now.getMonth() !== currentMonthIndex) {
    monthlyCount = 0;
    flow.set("monthlySignalCount", 0);
    flow.set("currentMonthIndex", now.getMonth());
}

let stateStore = flow.get("resourceStates") || {}; 
let teslaState = flow.get("teslaState") || "unknown";
let teslaCurrentAmps = flow.get("teslaAmps") || 0; 
let teslaGeofence = flow.get("teslaGeofence");
// Defaulta till Home om ok칛nt, f칬r s칛kerhets skull, men logga det.
if (teslaGeofence === undefined) teslaGeofence = TESLA_HOME_NAME;

let teslaMaxAmps = flow.get("teslaMaxAmps");
if (teslaMaxAmps === undefined || teslaMaxAmps === null) teslaMaxAmps = TESLA_DEFAULT_MAX;

let lastActionTime = flow.get("lastActionTime") || 0;
let lastHomeyCmdTime = flow.get("lastHomeyCmdTime") || 0;
let nowTs = Date.now();

// L칛s in m칛tdata
let m = msg.payload.metrics;
let currents = { 1: m.Current_L1, 2: m.Current_L2, 3: m.Current_L3 };
let totalPower = m.Power_Total;


// =========================================================================
// 3. TERMISK SIMULERING (FR칀N VFUSE)
// =========================================================================
let lastCalcTime = flow.get("lastCalcTime") || nowTs;
let dt = (nowTs - lastCalcTime) / 1000.0; 
if (dt > 10) dt = 1; // Skydd mot stora hopp
if (dt < 0) dt = 0;
flow.set("lastCalcTime", nowTs);

let fuseHeat = flow.get("fuseHeat") || {1:0, 2:0, 3:0};
let maxHeat = 0;

for (let ph = 1; ph <= 3; ph++) {
    let I = currents[ph];
    let loadRatioSquared = (I / FUSE_NOMINAL) * (I / FUSE_NOMINAL);
    // Low Pass Filter (I2t)
    let diff = loadRatioSquared - fuseHeat[ph];
    fuseHeat[ph] += diff * (dt / FUSE_TAU);

    if (fuseHeat[ph] < 0) fuseHeat[ph] = 0; 
    if (fuseHeat[ph] > maxHeat) maxHeat = fuseHeat[ph];
}
flow.set("fuseHeat", fuseHeat);


// =========================================================================
// 4. KOMMUNIKATION (FR칀N v28 - Robust)
// =========================================================================
let mqttOut = [];
let statusMsg = "";
let statusColor = "green";
let actionTaken = false;

function sendHomey(topic, payload, reason, isCritical) {
    if (monthlyCount >= CRITICAL_CUTOFF && !isCritical) {
        statusMsg += "BUDGET-STOP ";
        return false;
    }
    // Skicka inte om vi redan ligger p친 r칛tt v칛rde (sparar API)
    if (topic === TOPIC_HOMEY_SET && parseInt(payload) === teslaCurrentAmps) return false; 

    mqttOut.push({ topic: topic, payload: payload });
    if (topic === TOPIC_HOMEY_SET) flow.set("teslaAmps", parseInt(payload)); // Uppdatera minnet direkt

    flow.set("lastHomeyCmdTime", nowTs);
    monthlyCount++;
    flow.set("monthlySignalCount", monthlyCount);

    // Spara backup vid 칛ndring
    let backupPayload = JSON.stringify({ c: monthlyCount, m: new Date().getMonth(), fh: fuseHeat });
    mqttOut.push({ topic: TOPIC_STORAGE, payload: backupPayload, retain: true });

    statusMsg += reason + " ";
    return true;
}

// Hj칛lpfunktioner
function isTeslaActive() {
    // Kollar b친de state och geofence (v28 logic)
    let charging = (teslaState === "charging" || teslaState === "starting");
    // Case-insensitive check f칬r Geofence
    let isHome = (teslaGeofence.toLowerCase() === TESLA_HOME_NAME.toLowerCase());
    return charging && isHome; 
}

function resourceAffectsOverloadedPhase(resource, overloadedPhases) {
    return resource.phases.some(phase => overloadedPhases.includes(phase));
}

let timeSinceLast = nowTs - lastActionTime;
let minsSinceHomey = (nowTs - lastHomeyCmdTime) / 60000;

// Uppdatera status i objektet innan vi b칬rjar
resources.forEach(r => { r.status = stateStore[r.id] !== undefined ? stateStore[r.id] : null; });


// =========================================================================
// 5. LOGIK MOTOR (Sammanslagen)
// =========================================================================

// A. KRITISK NIV칀 (R칐D) - Baserat p친 V츿RME eller SUPER-칐VERSTR칐M
let maxAmpNow = Math.max(currents[1], currents[2], currents[3]);

if (maxHeat >= TEMP_CRITICAL || maxAmpNow >= AMP_PANIC) {
    statusColor = "red";
    // Identifiera vilka faser som brinner
    let criticalPhases = [];
    for(let ph=1; ph<=3; ph++) {
        if (fuseHeat[ph] >= TEMP_CRITICAL || currents[ph] >= AMP_PANIC) criticalPhases.push(ph);
    }

    statusMsg += `KRITISK F${criticalPhases.join(",")} (${(maxHeat*100).toFixed(0)}%)! `;

    resources.filter(r => resourceAffectsOverloadedPhase(r, criticalPhases)).forEach(r => {
        if (r.type === "mqtt" && r.status !== "off") {
            mqttOut.push({ topic: r.topic, payload: "off" });
            stateStore[r.id] = "off";
            statusMsg += "Klipp: " + r.name + ". ";
            actionTaken = true;
        } 
        else if (r.type === "tesla_homey" && isTeslaActive()) {
            // Vid kritisk niv친 stoppar vi bilen direkt
            if (sendHomey(TOPIC_HOMEY_STOP, "true", "STOPP Tesla", true)) actionTaken = true;
        }
    });
}

// B. REGLERING (ORANGE) - Baserat p친 V츿RME (>70%)
else if (maxHeat > TEMP_REGULATE) {
    statusColor = "orange";
    let timerLimit = DRY_RUN ? 500 : 3000;

    if (timeSinceLast > timerLimit) {
        let regulatePhases = [];
        for(let ph=1; ph<=3; ph++) if(fuseHeat[ph] > TEMP_REGULATE) regulatePhases.push(ph);

        let sortedResources = resources.sort((a, b) => a.shedOrder - b.shedOrder);

        for (let r of sortedResources) {
            if (actionTaken) break;

            if (resourceAffectsOverloadedPhase(r, regulatePhases)) {
                // 1. MQTT Laster
                if (r.type === "mqtt") {
                    if (r.status !== "off") {
                        mqttOut.push({ topic: r.topic, payload: "off" });
                        stateStore[r.id] = "off";
                        statusMsg += "St칛nger " + r.name + ". ";
                        actionTaken = true;
                    }
                } 
                // 2. Tesla (S칛nk str칬m mjukt)
                else if (r.type === "tesla_homey" && isTeslaActive()) {
                    if (teslaCurrentAmps > TESLA_MIN_AMPS) {
                        // R칛kna ut nytt m친l. Ju varmare, desto mer s칛nkning.
                        let step = (maxHeat > 0.85) ? 3 : 1;
                        let newTarget = teslaCurrentAmps - step;
                        if (newTarget < TESLA_MIN_AMPS) newTarget = TESLA_MIN_AMPS;

                        if (newTarget < teslaCurrentAmps) {
                            if (sendHomey(TOPIC_HOMEY_SET, String(newTarget), `S칛nk Tesla->${newTarget}A`, false)) actionTaken = true;
                        }
                    }
                    // Om vi redan 칛r p친 minsta str칬m och det fortfarande stiger...
                    // L친t loopen forts칛tta och kanske st칛nga n칛sta last (VVB)
                }
            }
        }
    }
    if(!actionTaken) statusMsg += `V칛rmereglering ${(maxHeat*100).toFixed(0)}%. `;
}

// C. EFFEKT (GUL) - Samma som v28 (Watt-begr칛nsning)
else if (totalPower > POWER_LIMIT) {
    statusColor = "yellow";
    let timerLimit = DRY_RUN ? 500 : 5000;

    if (timeSinceLast > timerLimit) {
        let sorted = resources.sort((a, b) => a.shedOrder - b.shedOrder); 

        for (let r of sorted) {
            if (actionTaken) break; 

            if (r.type === "mqtt" && r.status !== "off") {
                mqttOut.push({ topic: r.topic, payload: "off" });
                stateStore[r.id] = "off";
                statusMsg += "Effekt: St칛nger " + r.name + ". ";
                actionTaken = true;
            } 
            else if (r.type === "tesla_homey" && isTeslaActive()) {
                 if (teslaCurrentAmps > TESLA_MIN_AMPS) {
                     let newTarget = teslaCurrentAmps - 1; 
                     if (sendHomey(TOPIC_HOMEY_SET, String(newTarget), "Effekt: S칛nk bil", false)) actionTaken = true;
                 }
                 else {
                     // Om 칬vertr칛delsen 칛r stor (>500W) och vi ligger p친 minsta amp -> STOPPA
                     let overload = totalPower - POWER_LIMIT;
                     if (overload > 500) { 
                         if (sendHomey(TOPIC_HOMEY_STOP, "true", "Effekt: Stopp bil", false)) actionTaken = true;
                     }
                 }
            }
        }
    }
    if(!actionTaken) statusMsg += `H칬g effekt ${(totalPower/1000).toFixed(1)}kW. `;
}

// D. 칀TERST츿LLNING (GR칐N/BL칀) 
// Krav: V칛rme < 50% OCH Effekt OK
else {
    let heatOK = maxHeat < 0.50;
    let powerOK = totalPower < (POWER_LIMIT - POWER_RESET_BUFFER);

    if (heatOK && powerOK) {
        let timerLimit = DRY_RUN ? 500 : 10000;
        if (timeSinceLast > timerLimit) {
            let recoveryResources = resources.sort((a, b) => b.shedOrder - a.shedOrder); 
            for (let r of recoveryResources) {
                if (actionTaken) break; 

                // 1. MQTT Laster
                if (r.type === "mqtt" && r.status !== "on") {
                    mqttOut.push({ topic: r.topic, payload: "on" });
                    stateStore[r.id] = "on";
                    statusMsg += "칀ter: " + r.name + ". ";
                    actionTaken = true;
                }

                // 2. Tesla Hantering
                else if (r.type === "tesla_homey") {
                    // Fall A: Bilen laddar - 칐ka str칬mmen
                    if (isTeslaActive()) {
                         if (minsSinceHomey > API_COOLDOWN_MINS && teslaCurrentAmps < teslaMaxAmps) {
                             // Dubbelkolla att vi har str칬mmarginal ocks친, f칬r s칛kerhets skull
                             let maxCurrentNow = Math.max(currents[1], currents[2], currents[3]);
                             let ampsAvailable = FUSE_NOMINAL - maxCurrentNow;

                             if (ampsAvailable > 2) {
                                 let potential = teslaCurrentAmps + 1;
                                 if (potential > teslaMaxAmps) potential = teslaMaxAmps;
                                 if (potential > teslaCurrentAmps) {
                                     if (sendHomey(TOPIC_HOMEY_SET, String(potential), "Tesla->" + potential + "A", false)) actionTaken = true;
                                 }
                             }
                         }
                    }
                    // Fall B: Auto-Restart om den sover/stoppad
                    else if (["stopped", "asleep", "online"].includes(teslaState) && isTeslaActive() === false && teslaGeofence.toLowerCase() === TESLA_HOME_NAME.toLowerCase()) {
                         // H칛r anv칛nder vi den "virtuella" marginalen. Om v칛rmen 칛r l친g, k칬r!
                         if (minsSinceHomey > API_COOLDOWN_MINS && maxHeat < 0.40) {
                             // Vi startar bara om vi har effektutrymme ocks친
                             if (totalPower < (POWER_LIMIT - 4000)) {
                                 if (sendHomey(TOPIC_HOMEY_START, "true", "STARTA Tesla", false)) {
                                     sendHomey(TOPIC_HOMEY_SET, String(TESLA_MIN_AMPS), "Set " + TESLA_MIN_AMPS + "A", false);
                                     actionTaken = true;
                                 }
                             }
                         }
                    }
                }
            }
        }
    }

    if (!heatOK) {
        statusMsg += `Svalnar... (${(maxHeat*100).toFixed(0)}%)`;
        statusColor = "blue";
    } else if (!statusMsg && !actionTaken) {
        statusMsg = "Normal drift";
    }
}


// =========================================================================
// 6. OUTPUT & GRAFIK
// =========================================================================
flow.set("resourceStates", stateStore);
if (actionTaken) flow.set("lastActionTime", nowTs);

// Skicka temperaturer till Grafana (fr친n VFUSE)
mqttOut.push({ topic: "system/sips/fuse_temp_L1", payload: (fuseHeat[1]*100).toFixed(1) });
mqttOut.push({ topic: "system/sips/fuse_temp_L2", payload: (fuseHeat[2]*100).toFixed(1) });
mqttOut.push({ topic: "system/sips/fuse_temp_L3", payload: (fuseHeat[3]*100).toFixed(1) });

// Skicka rel칛-status till Grafana (fr친n v28)
let lm = { rad: stateStore["rad"]==="on"?1:0, vvb: stateStore["vvb"]==="on"?1:0, avf: stateStore["avf"]==="on"?1:0 };
let lastMetrics = flow.get("lastMetrics") || {};
if (JSON.stringify(lm) !== JSON.stringify(lastMetrics)) {
    if(lm.rad !== lastMetrics.rad) mqttOut.push({ topic: "homey/logic/radiators", payload: lm.rad });
    if(lm.vvb !== lastMetrics.vvb) mqttOut.push({ topic: "homey/logic/vvb", payload: lm.vvb });
    if(lm.avf !== lastMetrics.avf) mqttOut.push({ topic: "homey/logic/avf", payload: lm.avf });
    flow.set("lastMetrics", lm);
}

// Status text
let geoStatus = (teslaGeofence.toLowerCase() === TESLA_HOME_NAME.toLowerCase()) ? "H" : "A";
let budgetPercent = Math.round((monthlyCount / MONTHLY_LIMIT) * 100);
let infoTxt = ` [${(maxHeat*100).toFixed(0)}% Heat] [API:${monthlyCount}] [${geoStatus}]`;

node.status({
    fill: statusColor, 
    shape: (statusColor === "blue" || statusColor === "green") ? "dot" : "ring", 
    text: statusMsg + infoTxt
});

return [mqttOut, null];

// 1. Kontrollera om payload 칛r text (str칛ng) och g칬r om till objekt
if (typeof msg.payload === "string") {
    msg.payload = JSON.parse(msg.payload);
}

// 2. Plocka ut v칛rdet f칬r "c" och g칬r det till den nya payloaden
msg.payload = msg.payload.c; 

// Nu 칛r msg.payload bara siffran 93
return msg;

// 1. S칛kra upp att vi har ett objekt
let data = msg.payload;
if (typeof data === "string") {
    try {
        data = JSON.parse(data);
    } catch (e) {
        node.warn("Kunde inte parsa JSON f칬r Power_Total");
        return null;
    }
}

// 2. Kontrollera att s칬kv칛gen finns
if (data.metrics && data.metrics.Power_Total !== undefined) {
    // 3. Extrahera v칛rdet och tvinga till flyttal (float)
    let power = parseFloat(data.metrics.Power_Total);

    // 4. Skicka vidare endast om det 칛r ett giltigt tal
    if (!isNaN(power)) {
        msg.payload = power;
        return msg;
    }
}

// Om n친got saknas, skicka inget vidare (tystar fel i Influx)
return null;

// =================================================================
// UPDATE STATE & FAST FORWARD
// =================================================================
// 1. L칛ser in status direkt fr친n Shelly (n칛r knappen trycks).
// 2. Uppdaterar SIPS globala minne direkt (slipper v칛nta p친 loop).
// 3. Skickar vidare till Grafana/Homey direkt (slipper lagg i graf).
// =================================================================

// 1. KONFIGURATION: Karta mellan Shelly-namn och SIPS-ID
const idMap = {
    "vvb1": "vvb",
    "rad1": "rad",
    "avf1": "avf" 
};

// 2. IDENTIFIERA ENHET FR칀N TOPIC
// Topic format in: "shellies/avf1/relay/0"
let parts = msg.topic.split("/");
let deviceId = parts[1]; // H칛r hamnar "avf1", "rad1" eller "vvb1"
let sipsId = idMap[deviceId];

// Om enheten inte finns i v친r karta (t.ex. en lampa vi inte styr), avbryt.
if (!sipsId) return null;

// L칛s payload ("on" eller "off")
let actualStatus = msg.payload.toString(); 

// 3. UPPDATERA SIPS MINNE (Global Context)
// Detta 칛r kritiskt f칬r att SIPS ska veta statusen omedelbart
let stateStore = flow.get("resourceStates") || {};

// Vi uppdaterar bara om det beh칬vs, eller f칬r s칛kerhets skull
if (stateStore[sipsId] !== actualStatus) {
    stateStore[sipsId] = actualStatus;
    flow.set("resourceStates", stateStore);

    // Debug-utskrift s친 du ser att det fungerar i sidopanelen
    node.warn(`游댃 MANUELL SYNK: ${sipsId} (fysisk: ${deviceId}) 칛ndrades till ${actualStatus}`);
}

// 4. SKICKA TILL GRAFANA / HOMEY DIREKT
// Vi bygger om meddelandet f칬r att passa "homey/logic"-formatet

// Ny topic (t.ex. homey/logic/avf)
msg.topic = "homey/logic/" + sipsId;

// Konvertera payload: "on" -> 1, "off" -> 0
msg.payload = (actualStatus === "on") ? 1 : 0;

// VIKTIGA FLAGGOR
msg.retain = true; // Sparar v칛rdet i Mosquitto. Grafana visar r칛tt direkt vid omstart.
msg.qos = 1;       // Garanterar att meddelandet kommer fram.

// Skicka vidare till MQTT Out
return msg;

// 1. S칛kra upp att vi har ett objekt
let data = msg.payload;
if (typeof data === "string") {
    try {
        data = JSON.parse(data);
    } catch (e) {
        node.warn("Kunde inte parsa JSON f칬r Str칬mmar");
        return null;
    }
}

// 2. Kontrollera att metrics finns
if (!data.metrics) return null;

// 3. Hj칛lpfunktion f칬r att extrahera v칛rde
function getPhaseMsg(val) {
    let current = parseFloat(val);
    if (!isNaN(current)) {
        // Returnera ett nytt objekt med BARA v칛rdet i payload
        return { payload: current };
    }
    return null; // Skickar inget om v칛rdet 칛r ogiltigt (tystar fel)
}

// 4. Mappa utg친ngarna
// Shelly 3EM brukar heta Current_L1, Current_L2, Current_L3 i metrics
let out1 = getPhaseMsg(data.metrics.Current_L1); // Till Influx L1
let out2 = getPhaseMsg(data.metrics.Current_L2); // Till Influx L2
let out3 = getPhaseMsg(data.metrics.Current_L3); // Till Influx L3

// 5. Returnera array med 3 meddelanden
// Output 1 = Fas 1, Output 2 = Fas 2, Output 3 = Fas 3
return [out1, out2, out3];

// 1. S칛kra upp att vi har ett objekt
let data = msg.payload;
if (typeof data === "string") {
    try {
        data = JSON.parse(data);
    } catch (e) {
        node.warn("Kunde inte parsa JSON f칬r Effekt");
        return null;
    }
}

// 2. Kontrollera att metrics finns
if (!data.metrics) return null;

// 3. Hj칛lpfunktion f칬r att extrahera v칛rde
function getPhaseMsg(val) {
    let power = parseFloat(val);
    if (!isNaN(power)) {
        // Returnera ett nytt objekt med BARA v칛rdet i payload
        return { payload: power };
    }
    return null; // Skickar inget om v칛rdet 칛r ogiltigt
}

